import React from 'react';
import styled from 'styled-components';
import Navbar from './Navbar';

const HeroSection = () => {
  return (
    <HeroContainer>
      <HeroBackground>
        <GradientCircle />
      </HeroBackground>
      <Navbar />
      <HeroContent>
        <HeroHeading>
          AI-first solutions.
          <br />
          <AccentText>Human-first Impact.</AccentText>
        </HeroHeading>
        <HeroSubheading>
          We help organizations build intelligent systems right from custom AI agents to scalable cloud-native platforms. We will help you truly go from 0 --> 1
        </HeroSubheading>
        <ButtonGroup>
          <PrimaryButton>
            Schedule a Consultation
          </PrimaryButton>
          <SecondaryButton>
            Learn more
          </SecondaryButton>
        </ButtonGroup>
      </HeroContent>
    </HeroContainer>
  );
};

// Styled Components
const HeroContainer = styled.section`
  position: relative;
  height: 100vh;
  width: 100%;
  overflow: hidden;
  display: flex;
  flex-direction: column;
  background-color: #fff;
  color: #1a1a2e;
`;

const HeroBackground = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  z-index: 1;
`;

const GradientCircle = styled.div`
  position: absolute;
  top: 20%;
  right: -10%;
  width: 60vw;
  height: 60vw;
  border-radius: 50%;
  background: linear-gradient(135deg, rgba(255, 107, 107, 0.1), rgba(72, 52, 212, 0.1), rgba(19, 184, 119, 0.1));
  filter: blur(60px);
  z-index: -1;
`;

const HeroContent = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  padding: 0 20px;
  height: 100%;
  max-width: 900px;
  margin: 0 auto;
  z-index: 2;
`;

const HeroHeading = styled.h1`
  font-size: clamp(2.5rem, 5vw, 4.5rem);
  font-weight: 700;
  line-height: 1.1;
  margin-bottom: 24px;
  letter-spacing: -0.03em;
  color: #1a1a2e;

  @media (max-width: 768px) {
    font-size: 2.5rem;
  }
`;

const AccentText = styled.span`
  display: inline-block;
  background: linear-gradient(90deg, #4568dc, #b06ab3);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  color: transparent;
`;

const HeroSubheading = styled.p`
  font-size: clamp(1.1rem, 1.5vw, 1.3rem);
  line-height: 1.6;
  max-width: 650px;
  margin-bottom: 40px;
  color: #555;
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 16px;

  @media (max-width: 480px) {
    flex-direction: column;
    width: 100%;
    max-width: 300px;
  }
`;

const PrimaryButton = styled.button`
  background: linear-gradient(90deg, #4568dc, #b06ab3);
  color: white;
  font-size: 1rem;
  font-weight: 600;
  padding: 14px 28px;
  border-radius: 10px;
  transition: all 0.3s ease;
  box-shadow: 0px 4px 12px rgba(69, 104, 220, 0.2);

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0px 6px 14px rgba(69, 104, 220, 0.3);
  }
`;

const SecondaryButton = styled.button`
  background: transparent;
  color: #4568dc;
  font-size: 1rem;
  font-weight: 600;
  padding: 14px 28px;
  border-radius: 10px;
  transition: all 0.3s ease;
  border: 1px solid #4568dc;

  &:hover {
    background: rgba(69, 104, 220, 0.05);
  }
`;

export default HeroSection;
