import React, { useState, useEffect, useRef } from 'react';
import styled, { keyframes } from 'styled-components';
import { AIAgentsIcon, CloudServicesIcon, AppDevIcon } from './ServiceIcons';

const ServicesOverview = () => {
  const [activeService, setActiveService] = useState(0);
  const sectionRef = useRef(null);

  // Services data
  const services = [
    {
      id: 0,
      title: "AI Agents",
      description: "Inject your workflows with custom autonomous, secure agents with your organization's context.",
      icon: AIAgentsIcon,
      color: "#39B8B8"
    },
    {
      id: 1,
      title: "Cloud Services",
      description: "Migrate, optimize, and scale with expert cloud strategy.",
      icon: CloudServicesIcon,
      color: "#00A4C0"
    },
    {
      id: 2,
      title: "Web/Mobile App Development",
      description: "Build full-stack mobile/web apps with the latest stack.",
      icon: AppDevIcon,
      color: "#0090B0"
    }
  ];

  // Animation on scroll
  useEffect(() => {
    const observer = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          entry.target.classList.add('animate-in');
        }
      });
    }, { threshold: 0.2 });

    const section = sectionRef.current;
    if (section) {
      observer.observe(section);
    }

    return () => {
      if (section) {
        observer.unobserve(section);
      }
    };
  }, []);

  return (
    <SectionWrapper ref={sectionRef}>
      <Container>
        <SectionTitle>
          <Highlight>Core Capabilities</Highlight>
          <MainTitle>Where Innovation Meets Implementation</MainTitle>
        </SectionTitle>

        <ServicesContainer>
          <IndicatorTrack>
            {services.map((service, index) => (
              <Indicator 
                key={service.id}
                isActive={activeService === index}
                color={service.color}
              />
            ))}
          </IndicatorTrack>

          <ServicesTabs>
            {services.map((service, index) => (
              <ServiceTab 
                key={service.id}
                isActive={activeService === index}
                onClick={() => setActiveService(index)}
                color={service.color}
              >
                {service.title}
              </ServiceTab>
            ))}
          </ServicesTabs>

          <ContentArea>
            {services.map((service, index) => (
              <ServiceContent 
                key={service.id}
                isActive={activeService === index}
              >
                <ServiceIconContainer color={service.color}>
                  <IconWrapper>
                    {React.createElement(service.icon, { color: service.color })}
                  </IconWrapper>
                  <IconRing color={service.color} />
                </ServiceIconContainer>
                <ContentText>
                  <ServiceTitle color={service.color}>{service.title}</ServiceTitle>
                  <ServiceDescription>{service.description}</ServiceDescription>
                  <LearnMoreButton color={service.color}>
                    Explore {service.title}
                    <Arrow>â†’</Arrow>
                  </LearnMoreButton>
                </ContentText>
              </ServiceContent>
            ))}
          </ContentArea>
        </ServicesContainer>
      </Container>

      <BackgroundAccent />
    </SectionWrapper>
  );
};

// Animations
const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const pulse = keyframes`
  0% {
    transform: scale(1);
  }
  50% {
    transform: scale(1.05);
  }
  100% {
    transform: scale(1);
  }
`;

const spin = keyframes`
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
`;

// Styled Components
const SectionWrapper = styled.section`
  padding: 100px 20px;
  position: relative;
  overflow: hidden;
  background: linear-gradient(to bottom, #fff, #f8f9fa);
  opacity: 0;
  transform: translateY(30px);
  transition: opacity 0.8s ease, transform 0.8s ease;

  &.animate-in {
    opacity: 1;
    transform: translateY(0);
  }
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  position: relative;
  z-index: 2;
`;

const SectionTitle = styled.div`
  text-align: center;
  margin-bottom: 60px;
`;

const Highlight = styled.h3`
  font-size: 1.2rem;
  text-transform: uppercase;
  letter-spacing: 3px;
  margin-bottom: 16px;
  color: #39B8B8;
  font-weight: 600;
`;

const MainTitle = styled.h2`
  font-size: clamp(2rem, 4vw, 2.8rem);
  font-weight: 700;
  color: #1a1a2e;
  margin: 0;
  line-height: 1.2;
`;

const ServicesContainer = styled.div`
  display: flex;
  flex-direction: column;
  background: white;
  border-radius: 20px;
  box-shadow: 0 10px 40px rgba(0, 0, 0, 0.05);
  overflow: hidden;
`;

const IndicatorTrack = styled.div`
  display: flex;
  height: 4px;
  background: #f2f2f2;
`;

const Indicator = styled.div`
  flex: 1;
  height: 100%;
  background: ${props => props.isActive ? props.color : 'transparent'};
  transition: background 0.3s ease;
`;

const ServicesTabs = styled.div`
  display: flex;
  border-bottom: 1px solid #f0f0f0;

  @media (max-width: 768px) {
    flex-direction: column;
  }
`;

const ServiceTab = styled.button`
  flex: 1;
  padding: 20px;
  border: none;
  background: ${props => props.isActive ? 'white' : '#f8f9fa'};
  color: ${props => props.isActive ? props.color : '#555'};
  font-weight: ${props => props.isActive ? '600' : '500'};
  font-size: 1.1rem;
  cursor: pointer;
  transition: all 0.3s ease;
  position: relative;
  text-align: center;

  &:hover {
    background: ${props => props.isActive ? 'white' : '#f2f2f2'};
    color: ${props => props.color};
  }

  &::after {
    content: '';
    position: absolute;
    bottom: -1px;
    left: 0;
    width: 100%;
    height: 3px;
    background: ${props => props.isActive ? props.color : 'transparent'};
    transition: all 0.3s ease;
  }

  @media (max-width: 768px) {
    border-bottom: 1px solid #f0f0f0;

    &:last-child {
      border-bottom: none;
    }
  }
`;

const ContentArea = styled.div`
  position: relative;
  min-height: 300px;
  padding: 40px;

  @media (max-width: 768px) {
    padding: 30px 20px;
  }
`;

const ServiceContent = styled.div`
  display: ${props => props.isActive ? 'flex' : 'none'};
  animation: ${fadeIn} 0.5s ease;
  gap: 40px;

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 20px;
    align-items: center;
    text-align: center;
  }
`;

const ServiceIconContainer = styled.div`
  width: 120px;
  height: 120px;
  flex-shrink: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  margin-right: 20px;

  &:hover {
    animation: ${pulse} 1s ease-in-out;
  }

  @media (max-width: 768px) {
    margin-right: 0;
    margin-bottom: 20px;
  }
`;

const IconWrapper = styled.div`
  width: 60%;
  height: 60%;
  z-index: 2;
`;

const IconRing = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  border: 2px dashed ${props => props.color ? `rgba(${parseInt(props.color.slice(1, 3), 16)}, ${parseInt(props.color.slice(3, 5), 16)}, ${parseInt(props.color.slice(5, 7), 16)}, 0.3)` : 'rgba(57, 184, 184, 0.3)'};
  border-radius: 50%;
  animation: ${spin} 20s linear infinite;
`;

const ContentText = styled.div`
  flex: 1;
`;

const ServiceTitle = styled.h3`
  font-size: 1.8rem;
  font-weight: 700;
  margin-bottom: 16px;
  color: ${props => props.color};
`;

const ServiceDescription = styled.p`
  font-size: 1.1rem;
  line-height: 1.6;
  color: #555;
  margin-bottom: 24px;
`;

const LearnMoreButton = styled.button`
  background: transparent;
  border: none;
  color: ${props => props.color};
  font-weight: 600;
  font-size: 1rem;
  display: flex;
  align-items: center;
  padding: 0;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    opacity: 0.8;
  }

  @media (max-width: 768px) {
    margin: 0 auto;
  }
`;

const Arrow = styled.span`
  margin-left: 10px;
  transition: transform 0.3s ease;

  ${LearnMoreButton}:hover & {
    transform: translateX(5px);
  }
`;

const BackgroundAccent = styled.div`
  position: absolute;
  top: -50px;
  right: -100px;
  width: 500px;
  height: 500px;
  background: radial-gradient(circle, rgba(57, 184, 184, 0.05) 0%, rgba(0, 164, 192, 0.02) 50%, transparent 70%);
  border-radius: 50%;
  z-index: 1;
`;

export default ServicesOverview;
