import React, { useEffect, useRef } from 'react';
import styled, { keyframes } from 'styled-components';
import { motion } from 'framer-motion';

const Testimonials = () => {
  const sectionRef = useRef(null);

  // Example testimonials data
  const testimonials = [
    {
      id: 1,
      name: "Alex Chen",
      position: "CTO",
      company: "TechVision",
      avatar: "https://i.pravatar.cc/150?img=11",
      quote: "Aiolos Cloud transformed our entire infrastructure. The migration was seamless, and we've seen a 40% reduction in operational costs along with improved reliability."
    },
    {
      id: 2,
      name: "Sarah Rodriguez",
      position: "Head of Engineering",
      company: "DataSphere",
      avatar: "https://i.pravatar.cc/150?img=5",
      quote: "Working with Aiolos on our AI integration was a game-changer. Their technical expertise coupled with their commitment to delivery exceeded our expectations."
    },
    {
      id: 3,
      name: "Michael Wong",
      position: "Product Director",
      company: "InnovateTech",
      avatar: "https://i.pravatar.cc/150?img=12",
      quote: "The team at Aiolos built our mobile app from concept to launch in record time. Their AI-first approach gave us features our competitors still don't have."
    },
    {
      id: 4,
      name: "Priya Patel",
      position: "CEO",
      company: "HealthTech Solutions",
      avatar: "https://i.pravatar.cc/150?img=9",
      quote: "Aiolos understood our complex security requirements from day one. Their 'secure by design' approach gave us peace of mind throughout the development process."
    },
    {
      id: 5,
      name: "James Wilson",
      position: "Digital Transformation Lead",
      company: "Global Finance Group",
      avatar: "https://i.pravatar.cc/150?img=15",
      quote: "After two failed attempts with other vendors, Aiolos delivered a robust cloud solution that scaled with our growth. Their expertise is unmatched in the industry."
    },
    {
      id: 6,
      name: "Emily Nakamura",
      position: "VP of Technology",
      company: "RetailNow",
      avatar: "https://i.pravatar.cc/150?img=20",
      quote: "The SoftPOS solution Aiolos developed revolutionized our in-store experience. Transaction times reduced by 70% and our customers love the seamless checkout."
    }
  ];

  // Animation on scroll
  useEffect(() => {
    const observer = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          entry.target.classList.add('animate-in');
        }
      });
    }, { threshold: 0.2 });

    const section = sectionRef.current;
    if (section) {
      observer.observe(section);
    }

    return () => {
      if (section) {
        observer.unobserve(section);
      }
    };
  }, []);

  return (
    <SectionWrapper ref={sectionRef}>
      <Container>
        <SectionTitle>
          <Highlight>Testimonials</Highlight>
          <MainTitle>Loved by world-class teams</MainTitle>
          <Subtitle>What our clients say about our work and collaboration</Subtitle>
        </SectionTitle>

        <TestimonialsContainer>
          <ScrollingRow>
            {testimonials.slice(0, 3).map((testimonial) => (
              <TestimonialCard key={testimonial.id}>
                <QuoteIcon>"</QuoteIcon>
                <TestimonialText>{testimonial.quote}</TestimonialText>
                <ClientInfo>
                  <Avatar src={testimonial.avatar} alt={testimonial.name} />
                  <ClientDetails>
                    <ClientName>{testimonial.name}</ClientName>
                    <ClientPosition>{testimonial.position}, {testimonial.company}</ClientPosition>
                  </ClientDetails>
                </ClientInfo>
              </TestimonialCard>
            ))}

            {/* Duplicate first few cards to create continuous scrolling effect */}
            {testimonials.slice(0, 1).map((testimonial) => (
              <TestimonialCard key={`duplicate-${testimonial.id}`}>
                <QuoteIcon>"</QuoteIcon>
                <TestimonialText>{testimonial.quote}</TestimonialText>
                <ClientInfo>
                  <Avatar src={testimonial.avatar} alt={testimonial.name} />
                  <ClientDetails>
                    <ClientName>{testimonial.name}</ClientName>
                    <ClientPosition>{testimonial.position}, {testimonial.company}</ClientPosition>
                  </ClientDetails>
                </ClientInfo>
              </TestimonialCard>
            ))}
          </ScrollingRow>

          <ScrollingRowReverse>
            {testimonials.slice(3, 6).map((testimonial) => (
              <TestimonialCard key={testimonial.id}>
                <QuoteIcon>"</QuoteIcon>
                <TestimonialText>{testimonial.quote}</TestimonialText>
                <ClientInfo>
                  <Avatar src={testimonial.avatar} alt={testimonial.name} />
                  <ClientDetails>
                    <ClientName>{testimonial.name}</ClientName>
                    <ClientPosition>{testimonial.position}, {testimonial.company}</ClientPosition>
                  </ClientDetails>
                </ClientInfo>
              </TestimonialCard>
            ))}

            {/* Duplicate first few cards to create continuous scrolling effect */}
            {testimonials.slice(3, 4).map((testimonial) => (
              <TestimonialCard key={`duplicate-${testimonial.id}`}>
                <QuoteIcon>"</QuoteIcon>
                <TestimonialText>{testimonial.quote}</TestimonialText>
                <ClientInfo>
                  <Avatar src={testimonial.avatar} alt={testimonial.name} />
                  <ClientDetails>
                    <ClientName>{testimonial.name}</ClientName>
                    <ClientPosition>{testimonial.position}, {testimonial.company}</ClientPosition>
                  </ClientDetails>
                </ClientInfo>
              </TestimonialCard>
            ))}
          </ScrollingRowReverse>
        </TestimonialsContainer>
      </Container>
    </SectionWrapper>
  );
};

// Animation keyframes
const scrollLeft = keyframes`
  0% { transform: translateX(0); }
  100% { transform: translateX(-100%); }
`;

const scrollRight = keyframes`
  0% { transform: translateX(-100%); }
  100% { transform: translateX(0); }
`;

// Styled Components
const SectionWrapper = styled.section`
  padding: 120px 20px;
  background: #141414;
  color: #fff;
  position: relative;
  overflow: hidden;
  opacity: 0;
  transform: translateY(30px);
  transition: opacity 0.8s ease, transform 0.8s ease;

  &.animate-in {
    opacity: 1;
    transform: translateY(0);
  }
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  position: relative;
  z-index: 2;
`;

const SectionTitle = styled.div`
  text-align: center;
  margin-bottom: 80px;
`;

const Highlight = styled.h3`
  font-size: 1.2rem;
  text-transform: uppercase;
  letter-spacing: 3px;
  margin-bottom: 16px;
  color: #39B8B8;
  font-weight: 600;
`;

const MainTitle = styled.h2`
  font-size: clamp(2rem, 4vw, 2.8rem);
  font-weight: 700;
  color: #fff;
  margin: 0;
  line-height: 1.2;
`;

const Subtitle = styled.p`
  font-size: clamp(1.1rem, 2vw, 1.3rem);
  color: #aaa;
  font-weight: 500;
  margin-top: 16px;
`;

const TestimonialsContainer = styled.div`
  position: relative;
  width: 100%;
  overflow: hidden;
`;

const ScrollingRow = styled.div`
  display: flex;
  animation: ${scrollLeft} 40s linear infinite;
  width: fit-content;
  margin-bottom: 30px;

  &:hover {
    animation-play-state: paused;
  }
`;

const ScrollingRowReverse = styled.div`
  display: flex;
  animation: ${scrollRight} 40s linear infinite;
  width: fit-content;

  &:hover {
    animation-play-state: paused;
  }
`;

const TestimonialCard = styled.div`
  min-width: 380px;
  max-width: 380px;
  background: rgba(255, 255, 255, 0.05);
  border-radius: 12px;
