import React, { useState, useEffect, useRef } from 'react';
import styled, { keyframes } from 'styled-components';
import { motion } from 'framer-motion';

const CTABanner = () => {
  const [isHovered, setIsHovered] = useState(false);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const bannerRef = useRef(null);
  const buttonRef = useRef(null);

  // Handle mouse movement on the banner
  const handleMouseMove = (e) => {
    if (!bannerRef.current || !buttonRef.current) return;

    const rect = bannerRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    setMousePosition({ x, y });

    // Subtle button movement based on mouse position
    if (!isHovered) {
      const buttonRect = buttonRef.current.getBoundingClientRect();
      const buttonCenterX = buttonRect.left + buttonRect.width / 2 - rect.left;
      const buttonCenterY = buttonRect.top + buttonRect.height / 2 - rect.top;

      const deltaX = (x - buttonCenterX) / 30;
      const deltaY = (y - buttonCenterY) / 30;

      buttonRef.current.style.transform = `translate(${deltaX}px, ${deltaY}px)`;
    } else {
      buttonRef.current.style.transform = 'translate(0, 0)';
    }
  };

  useEffect(() => {
    const handleMouseLeave = () => {
      if (buttonRef.current) {
        buttonRef.current.style.transform = 'translate(0, 0)';
      }
    };

    const banner = bannerRef.current;
    if (banner) {
      banner.addEventListener('mouseleave', handleMouseLeave);
    }

    return () => {
      if (banner) {
        banner.removeEventListener('mouseleave', handleMouseLeave);
      }
    };
  }, []);

  return (
    <BannerWrapper 
      ref={bannerRef}
      onMouseMove={handleMouseMove}
    >
      <BackgroundPattern />
      <DiagonalLines />

      <ContentContainer>
        <HeadingWrapper>
          <MainHeading>
            Start your AI journey
          </MainHeading>
          <SubHeading>
            with Aiolos today
          </SubHeading>
        </HeadingWrapper>

        <ButtonContainer>
          <ContactButton 
            ref={buttonRef}
            onMouseEnter={() => setIsHovered(true)}
            onMouseLeave={() => setIsHovered(false)}
            as={motion.a}
            href="#contact"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.98 }}
          >
            Contact Us
            <ButtonArrow isHovered={isHovered}>→</ButtonArrow>
          </ContactButton>

          {/* Animated elements to draw attention to the button */}
          <ButtonHighlight isHovered={isHovered} />

          <AttentionRings>
            {[...Array(3)].map((_, i) => (
              <AttentionRing key={i} delay={i * 0.5} />
            ))}
          </AttentionRings>
        </ButtonContainer>
      </ContentContainer>

      {/* Floating particles */}
      {[...Array(10)].map((_, i) => (
        <Particle 
          key={i}
          size={5 + Math.random() * 8}
          top={10 + Math.random() * 80}
          left={Math.random() * 100}
          duration={15 + Math.random() * 20}
          delay={Math.random() * 5}
        />
      ))}
    </BannerWrapper>
  );
};

// Keyframes for animations
const float = keyframes`
  0% { transform: translateY(0px) translateX(0px); }
  25% { transform: translateY(-10px) translateX(5px); }
  50% { transform: translateY(0px) translateX(10px); }
  75% { transform: translateY(10px) translateX(5px); }
  100% { transform: translateY(0px) translateX(0px); }
`;

const pulse = keyframes`
  0% { transform: scale(1); opacity: 1; }
  50% { transform: scale(1.05); opacity: 0.8; }
  100% { transform: scale(1); opacity: 1; }
`;

const shimmer = keyframes`
  0% { background-position: -100% 0; }
  100% { background-position: 200% 0; }
`;

const ring = keyframes`
  0% { transform: scale(0.8); opacity: 0.8; }
  100% { transform: scale(1.5); opacity: 0; }
`;

const moveUpDown = keyframes`
  0%, 100% { transform: translateY(0); }
  50% { transform: translateY(-15px); }
`;

// Styled Components
const BannerWrapper = styled.section`
  position: relative;
  background: linear-gradient(135deg, #2563eb, #1e40af);
  color: white;
  padding: 100px 20px;
  overflow: hidden;
  min-height: 400px;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const BackgroundPattern = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-image: radial-gradient(circle at 25% 25%, rgba(255, 255, 255, 0.1) 1px, transparent 1px),
    radial-gradient(circle at 75% 75%, rgba(255, 255, 255, 0.1) 1px, transparent 1px);
  background-size: 40px 40px;
  z-index: 1;
`;

const DiagonalLines = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-image: linear-gradient(
    135deg,
    rgba(255, 255, 255, 0.05) 25%,
    transparent 25%,
    transparent 50%,
    rgba(255, 255, 255, 0.05) 50%,
    rgba(255, 255, 255, 0.05) 75%,
    transparent 75%,
    transparent
  );
  background-size: 100px 100px;
  z-index: 1;
`;

const ContentContainer = styled.div`
  position: relative;
  z-index: 2;
  max-width: 900px;
  text-align: center;
`;

const HeadingWrapper = styled.div`
  margin-bottom: 50px;
  animation: ${float} 10s ease-in-out infinite;
`;

const MainHeading = styled.h2`
  font-size: clamp(2.5rem, 6vw, 4rem);
  font-weight: 700;
  margin: 0;
  text-shadow: 0 2px 10px rgba(0, 0, 0, 0.2);
  background: linear-gradient(to right, #ffffff, #e0e7ff, #ffffff);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-size: 200% auto;
  animation: ${shimmer} 5s linear infinite;
`;

const SubHeading = styled.h3`
  font-size: clamp(1.5rem, 4vw, 3rem);
  font-weight: 600;
  margin: 10px 0 0;
  color: rgba(255, 255, 255, 0.9);
`;

const ButtonContainer = styled.div`
  position: relative;
  display: inline-block;
`;

const ContactButton = styled.a`
  display: inline-flex;
  align-items: center;
  justify-content: center;
  padding: 16px 32px;
  background-color: white;
  color: #1e40af;
  border: none;
  border-radius: 50px;
  font-size: 1.2rem;
  font-weight: 600;
  text-decoration: none;
  cursor: pointer;
  box-shadow: 0 5px 25px rgba(0, 0, 0, 0.2);
  transition: all 0.3s ease;
  position: relative;
  z-index: 10;

  &:hover {
    background-color: #f9fafb;
    box-shadow: 0 8px 35px rgba(0, 0, 0, 0.3);
    transform: translateY(-2px);
  }
`;

const ButtonArrow = styled.span`
  margin-left: 8px;
  transition: transform 0.3s ease;
  transform: translateX(${props => props.isHovered ? '5px' : '0'});
`;

const ButtonHighlight = styled.div`
  position: absolute;
  top: -5px;
  left: -5px;
  right: -5px;
  bottom: -5px;
  border-radius: 50px;
  border: 2px solid white;
  opacity: ${props => props.isHovered ? 0.7 : 0};
  transform: scale(${props => props.isHovered ? 1.1 : 1});
  transition: all 0.3s ease;
  pointer-events: none;
  z-index: 9;
`;

const AttentionRings = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  z-index: 5;
  pointer-events: none;
`;

const AttentionRing = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  border: 2px solid white;
  border-radius: 50px;
  opacity: 0.8;
  animation: ${ring} 3s infinite ease-out;
  animation-delay: ${props => props.delay}s;
`;

const Particle = styled.div`
  position: absolute;
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  background-color: rgba(255, 255, 255, 0.5);
  border-radius: 50%;
  top: ${props => props.top}%;
  left: ${props => props.left}%;
  animation: ${moveUpDown} ${props => props.duration}s ease-in-out infinite;
  animation-delay: ${props => props.delay}s;
  z-index: 1;
  pointer-events: none;
`;

export default CTABanner;
