import React, { useState, useRef, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import Logo from './Logo';

const Navbar = () => {
  const [activeLink, setActiveLink] = useState('home');
  const [isServicesOpen, setIsServicesOpen] = useState(false);
  const dropdownRef = useRef(null);

  const handleLinkClick = (linkName, e) => {
    e.preventDefault();
    setActiveLink(linkName);
    if (linkName !== 'services') {
      setIsServicesOpen(false);
    }
  };

  const toggleServicesDropdown = (e) => {
    e.preventDefault();
    setIsServicesOpen(!isServicesOpen);
    setActiveLink('services');
  };

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsServicesOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [dropdownRef]);

  return (
    <NavbarContainer>
      <Logo />
      <NavLinks>
        <NavItem isActive={activeLink === 'home'}>
          <NavLink 
            href="/" 
            onClick={(e) => handleLinkClick('home', e)}
            isActive={activeLink === 'home'}
          >
            Home
            {activeLink === 'home' && <ActiveIndicator />}
          </NavLink>
        </NavItem>
        <NavItem isActive={activeLink === 'about'}>
          <NavLink 
            href="/about" 
            onClick={(e) => handleLinkClick('about', e)}
            isActive={activeLink === 'about'}
          >
            About Us
            {activeLink === 'about' && <ActiveIndicator />}
          </NavLink>
        </NavItem>
        <NavItem isActive={activeLink === 'services'} ref={dropdownRef}>
          <NavLink 
            href="/services" 
            onClick={toggleServicesDropdown}
            isActive={activeLink === 'services'}
            hasDropdown={true}
          >
            Services <DropdownIcon isOpen={isServicesOpen}>â–¼</DropdownIcon>
            {activeLink === 'services' && <ActiveIndicator />}
          </NavLink>
          {isServicesOpen && (
            <DropdownMenu>
              <DropdownTriangle />
              <DropdownItem>
                <DropdownLink 
                  href="/services/ai-agents"
                  onClick={(e) => {
                    e.preventDefault();
                    // Handle navigation in a real app
                    setIsServicesOpen(false);
                  }}
                >
                  AI Agents
                </DropdownLink>
              </DropdownItem>
              <DropdownItem>
                <DropdownLink 
                  href="/services/machine-learning"
                  onClick={(e) => {
                    e.preventDefault();
                    // Handle navigation in a real app
                    setIsServicesOpen(false);
                  }}
                >
                  Machine Learning
                </DropdownLink>
              </DropdownItem>
              <DropdownItem>
                <DropdownLink 
                  href="/services/cloud-services"
                  onClick={(e) => {
                    e.preventDefault();
                    // Handle navigation in a real app
                    setIsServicesOpen(false);
                  }}
                >
                  Cloud Services
                </DropdownLink>
              </DropdownItem>
              <DropdownItem>
                <DropdownLink 
                  href="/services/mobile-app-development"
                  onClick={(e) => {
                    e.preventDefault();
                    // Handle navigation in a real app
                    setIsServicesOpen(false);
                  }}
                >
                  Mobile App Development
                </DropdownLink>
              </DropdownItem>
              <DropdownItem>
                <DropdownLink 
                  href="/services/web-app-development"
                  onClick={(e) => {
                    e.preventDefault();
                    // Handle navigation in a real app
                    setIsServicesOpen(false);
                  }}
                >
                  Web App Development
                </DropdownLink>
              </DropdownItem>
              <DropdownItem>
                <DropdownLink 
                  href="/services/ui-ux-design"
                  onClick={(e) => {
                    e.preventDefault();
                    // Handle navigation in a real app
                    setIsServicesOpen(false);
                  }}
                >
                  UI/UX Design
                </DropdownLink>
              </DropdownItem>
            </DropdownMenu>
          )}
        </NavItem>
        <NavItem isActive={activeLink === 'contact'}>
          <NavLink 
            href="/contact" 
            onClick={(e) => handleLinkClick('contact', e)}
            isActive={activeLink === 'contact'}
          >
            Contact Us
            {activeLink === 'contact' && <ActiveIndicator />}
          </NavLink>
        </NavItem>
      </NavLinks>
      <CtaButton href="/schedule">Schedule a call</CtaButton>
    </NavbarContainer>
  );
};

// Animations
const fadeIn = keyframes`
  from {
    opacity: 0;
    width: 0;
  }
  to {
    opacity: 1;
    width: 100%;
  }
`;

const fadeInDown = keyframes`
  from {
    opacity: 0;
    transform: translateY(-10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const rotateAnimation = keyframes`
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(180deg);
  }
`;

// Styled Components
const NavbarContainer = styled.nav`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px 40px;
  position: relative;
  z-index: 100;
  width: 100%;
  backdrop-filter: blur(5px);
  transition: all 0.3s ease;

  @media (max-width: 768px) {
    padding: 20px;
  }
`;

const NavLinks = styled.ul`
  display: flex;
  gap: 30px;
  margin: 0;
  padding: 0;

  @media (max-width: 1024px) {
    gap: 20px;
  }

  @media (max-width: 768px) {
    display: none;
  }
`;

const NavItem = styled.li`
  position: relative;
  list-style: none;
`;

const ActiveIndicator = styled.div`
  position: absolute;
  bottom: -5px;
  left: 0;
  height: 2px;
  background: linear-gradient(90deg, #39B8B8, #00A4C0);
  border-radius: 2px;
  animation: ${fadeIn} 0.3s ease-in-out;
`;

const DropdownIcon = styled.span`
  font-size: 0.7rem;
  margin-left: 3px;
  transition: transform 0.3s ease;
  transform: ${props => props.isOpen ? 'rotate(180deg)' : 'rotate(0)'};
  display: inline-block;
`;

const NavLink = styled.a`
  color: ${props => props.isActive ? '#39B8B8' : '#555'};
  font-weight: ${props => props.isActive ? '600' : '500'};
  font-size: 0.9rem;
  transition: all 0.3s ease;
  padding: 5px 0;
  position: relative;
  display: flex;
  align-items: center;
  gap: 5px;

  &:hover {
    color: #39B8B8;
  }
`;

const DropdownMenu = styled.ul`
  position: absolute;
  top: 100%;
  left: 50%;
  transform: translateX(-50%);
  min-width: 220px;
  background: white;
  border-radius: 8px;
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.1);
  padding: 10px 0;
  margin-top: 15px;
  animation: ${fadeInDown} 0.3s ease-out;
  z-index: 20;
`;

const DropdownTriangle = styled.div`
  position: absolute;
  top: -8px;
  left: 50%;
  transform: translateX(-50%);
  width: 0;
  height: 0;
  border-left: 8px solid transparent;
  border-right: 8px solid transparent;
  border-bottom: 8px solid white;
`;

const DropdownItem = styled.li`
  list-style: none;
`;

const DropdownLink = styled.a`
  display: block;
  padding: 10px 20px;
  color: #555;
  font-size: 0.9rem;
  transition: all 0.2s ease;
  text-align: left;

  &:hover {
    background: rgba(57, 184, 184, 0.05);
    color: #39B8B8;
  }
`;

const CtaButton = styled.a`
  background: linear-gradient(90deg, #39B8B8, #00A4C0);
  color: white;
  font-size: 0.9rem;
  font-weight: 600;
  padding: 10px 20px;
  border-radius: 8px;
  transition: all 0.3s ease;
  box-shadow: 0px 4px 12px rgba(0, 180, 216, 0.2);
  text-decoration: none;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0px 6px 14px rgba(0, 180, 216, 0.3);
  }

  @media (max-width: 768px) {
    padding: 8px 16px;
    font-size: 0.85rem;
  }
`;

export default Navbar;
