import React, { useEffect, useRef, useState } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { AIFirstIcon, SecureIcon, ScaleIcon, ExpertiseIcon } from './WhyAiolosIcons';

const WhyAiolos = () => {
  const [activeStep, setActiveStep] = useState(0);
  const sectionRef = useRef(null);
  const intervalRef = useRef(null);

  // Pipeline steps data
  const pipelineSteps = [
    {
      id: 0,
      title: "AI-First Design Process",
      description: "AI is at the core of everything we build. Right from agents to MCPs, we leverage the latest tech to ensure you and your customers are delighted.",
      icon: AIFirstIcon,
      color: "#39B8B8"
    },
    {
      id: 1,
      title: "Secure by Design",
      description: "From healthcare to fintech, we build with trust.",
      icon: SecureIcon,
      color: "#00A4C0"
    },
    {
      id: 2,
      title: "Results that Scale",
      description: "From MVP to enterprise-grade systems.",
      icon: ScaleIcon,
      color: "#0090B0"
    },
    {
      id: 3,
      title: "25+ Years of Expertise",
      description: "Work directly with a team of experts, not middle layers.",
      icon: ExpertiseIcon,
      color: "#007A94"
    }
  ];

  // Auto-rotate through pipeline steps
  useEffect(() => {
    intervalRef.current = setInterval(() => {
      setActiveStep(prev => (prev + 1) % pipelineSteps.length);
    }, 3500);

    return () => {
      if (intervalRef.current) clearInterval(intervalRef.current);
    };
  }, [pipelineSteps.length]);

  // Animation on scroll
  useEffect(() => {
    const observer = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          entry.target.classList.add('animate-in');
        }
      });
    }, { threshold: 0.2 });

    const section = sectionRef.current;
    if (section) {
      observer.observe(section);
    }

    return () => {
      if (section) {
        observer.unobserve(section);
      }
    };
  }, []);

  return (
    <SectionWrapper ref={sectionRef}>
      <Container>
        <SectionTitle>
          <Highlight>The Aiolos Advantage</Highlight>
          <MainTitle>Pipeline of Excellence</MainTitle>
          <Subtitle>Where innovation flows through every project stage</Subtitle>
        </SectionTitle>

        <PipelineContainer>
          <PipelinePath>
            {pipelineSteps.map((step, index) => (
              <PathSection 
                key={step.id} 
                isActive={index <= activeStep}
                color={step.color}
                delay={index * 0.5}
              />
            ))}
          </PipelinePath>

          {pipelineSteps.map((step, index) => (
            <PipelineNode 
              key={step.id}
              as={motion.div}
              initial={{ scale: 0, opacity: 0 }}
              animate={{ 
                scale: index <= activeStep ? 1 : 0.5, 
                opacity: index <= activeStep ? 1 : 0.3,
              }}
              transition={{
                type: "spring",
                stiffness: 260,
                damping: 20,
                delay: index * 0.5
              }}
              color={step.color}
              onClick={() => setActiveStep(index)}
              isActive={index === activeStep}
              style={{ left: `${(index / (pipelineSteps.length - 1)) * 100}%` }}
            >
              <NodeIcon>
                {React.createElement(step.icon, { color: step.color })}
              </NodeIcon>
              {index === activeStep && (
                <PulseRing color={step.color} />
              )}
            </PipelineNode>
          ))}

          <AnimatePresence mode="wait">
            <HighlightedStep
              as={motion.div}
              key={activeStep}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ duration: 0.5 }}
            >
              <StepHeader>
                <StepCount color={pipelineSteps[activeStep].color}>
                  {(activeStep + 1).toString().padStart(2, '0')}
                </StepCount>
                <StepTitle color={pipelineSteps[activeStep].color}>
                  {pipelineSteps[activeStep].title}
                </StepTitle>
              </StepHeader>
              <StepDescription>
                {pipelineSteps[activeStep].description}
              </StepDescription>
              <FlowIndicator>
                <FlowDot delay={0} />
                <FlowDot delay={0.5} />
                <FlowDot delay={1} />
              </FlowIndicator>
            </HighlightedStep>
          </AnimatePresence>
        </PipelineContainer>

        <DataFlowLines>
          {[...Array(8)].map((_, i) => (
            <FlowLine 
              key={i}
              as={motion.div}
              initial={{ width: 0 }}
              animate={{ width: '100%' }}
              transition={{ 
                duration: 10,
                delay: i * 0.7,
                repeat: Infinity,
                ease: "linear"
              }}
              top={`${10 + (i * 10)}%`}
            />
          ))}
        </DataFlowLines>
      </Container>

      <BackgroundElements>
        <DataCircle top="15%" left="10%" size="100px" delay={0.2} />
        <DataCircle top="60%" left="15%" size="150px" delay={0.5} />
        <DataCircle top="20%" right="8%" size="120px" delay={0.8} />
        <DataCircle top="70%" right="12%" size="80px" delay={1.2} />
      </BackgroundElements>
    </SectionWrapper>
  );
};

const pulseAnimation = `
  @keyframes pulse {
    0% {
      transform: scale(0.95);
      box-shadow: 0 0 0 0 rgba(0, 164, 192, 0.7);
    }

    70% {
      transform: scale(1);
      box-shadow: 0 0 0 10px rgba(0, 164, 192, 0);
    }

    100% {
      transform: scale(0.95);
      box-shadow: 0 0 0 0 rgba(0, 164, 192, 0);
    }
  }
`;

const flowAnimation = `
  @keyframes flowingDots {
    0% {
      opacity: 0;
      transform: translateX(-100%);
    }
    50% {
      opacity: 1;
    }
    100% {
      opacity: 0;
      transform: translateX(100%);
