import React from 'react';
import styled from 'styled-components';
import Navbar from './Navbar';

const HeroSection = () => {
  return (
    <HeroContainer>
      <HeroBackground>
        <DotPattern />
      </HeroBackground>
      <Navbar />
      <HeroContent>
        <HeroHeading>
          <span>EMPOWERING YOUR</span>
          <br />
          <span>BUSINESS THROUGH</span>
          <br />
          <AccentText>AI FIRST SOLUTIONS</AccentText>
        </HeroHeading>
        <HeroSubheading>
          Our custom AI solutions transform how work happens, providing <strong>measurable ROI</strong> for your business.
        </HeroSubheading>
        <CTAButton>
          <ArrowIcon>â†’</ArrowIcon>
          Discover Aiolos
        </CTAButton>
      </HeroContent>
    </HeroContainer>
  );
};

// Styled Components
const HeroContainer = styled.section`
  position: relative;
  height: 100vh;
  width: 100%;
  overflow: hidden;
  display: flex;
  flex-direction: column;
  background-color: #000;
`;

const HeroBackground = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  z-index: 1;
`;

const DotPattern = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 700px;
  height: 700px;
  border-radius: 50%;
  background-image: radial-gradient(#0a3b3a 1px, transparent 1px);
  background-size: 20px 20px;
  opacity: 0.3;
  z-index: -1;
`;

const HeroContent = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  padding: 0 20px;
  height: 100%;
  max-width: 900px;
  margin: 0 auto;
  z-index: 2;
`;

const HeroHeading = styled.h1`
  font-size: clamp(2.5rem, 5vw, 4rem);
  font-weight: 700;
  line-height: 1.2;
  margin-bottom: 24px;
  letter-spacing: -0.03em;

  @media (max-width: 768px) {
    font-size: 2.5rem;
  }
`;

const AccentText = styled.span`
  display: inline-block;
  color: #2ec4b6;
  margin-top: 10px;
`;

const HeroSubheading = styled.p`
  font-size: clamp(1rem, 1.5vw, 1.25rem);
  line-height: 1.6;
  max-width: 600px;
  margin-bottom: 40px;
  color: rgba(255, 255, 255, 0.8);
`;

const CTAButton = styled.button`
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: rgba(46, 196, 182, 0.1);
  color: #2ec4b6;
  font-size: 1rem;
  font-weight: 600;
  padding: 12px 24px;
  border-radius: 9999px;
  transition: all 0.3s ease;
  border: 1px solid rgba(46, 196, 182, 0.3);

  &:hover {
    background-color: rgba(46, 196, 182, 0.2);
  }
`;

const ArrowIcon = styled.span`
  margin-right: 8px;
  font-size: 1.2rem;
`;

export default HeroSection;
